import { useGraphStates } from './GraphsContext';

export const useNodeFunctions = () => {

    const {
        nodes, edges, startingText, nodeCount, setClickedTraversal, setClickedMST, setText, setNodes,
        setNodeCount, setVisitedNodes, setVisitedEdges, defaultEdgeColor, setIsRemovingEdge, setSelectedNode, 
        dragging, setEdges, algorithmRunning, isRemovingEdge, setAdjList, isAddingEdge, setIsAddingEdge,
        selectedNode, isDirected, dfs, startNode, setIsDFS, bfs, setIsBFS, setIsPrim,
        animatePrimsAlgorithm, endNode, setEndNode, tsp, setIsTSP,
        setStartNode, findShortestPath, setIsShortestPath, setDragging,
        isTSP, isBFS, isDFS, isPrim, isShortestPath, setSliderValue, sliderValueRef
    } = useGraphStates();

    // Function to add a new node to the graph
    const addNode = () => {
        if(algorithmRunning){
            return;
        }

        if(nodes.length >= 20){
            alert("too many nodes");
            return;
        }

        setText(startingText);
        const newNode = {
            id: nodeCount,
            x: Math.random() * 480,
            y: Math.random() * 480,
        };
        setNodes([...nodes, newNode]);
        setAdjList(prevAdjList => ({ ...prevAdjList, [newNode.id]: [] }));
        setNodeCount(nodeCount + 1);
    };

    // Function to generate a random graph
    const generateGraph = () => {
        if(algorithmRunning || isRemovingEdge){
            return;
        }

        var numNodes = 0;
        var numEdges = -1;

        do{
            const response = prompt("Enter the number of nodes for the graph:", "");
            if(response === null){
                return;
            }
            if(isNaN(response)){
                alert("Invalid input. Please enter numbers only");
                continue;
            }

            if(response <= 0 || response > 20){
                alert("Invalid input. Number of nodes must be between 1 and 20");
                continue;
            }

            numNodes = response;

        }while(numNodes === 0);

        do{
            const response = prompt("Enter the number of edges for the graph:", "");
            if(response === null){
                return;
            }

            if(isNaN(response)){
                alert("Invalid input. Please enter numbers only");
                continue;
            }

            if(!isDirected){
                if(response > ((numNodes * (numNodes - 1)) / 2)){
                    alert("Invalid input. Too many edges for the undirected graph");
                    continue;
                }
            }else{
                if(response > ((numNodes * (numNodes - 1)))){
                    alert("Invalid input. Too many edges for the directed graph");
                    continue;
                }
            }

            if(response < 0){
                alert("Invalid input. Not enough edges");
                continue;
            }

            numEdges = response;

        }while(numEdges === -1);

        setNodes([]);
        setEdges([]);
        setAdjList({});
        setNodeCount(0);
        setVisitedNodes([]);
        setVisitedEdges([]);
        setSelectedNode(null);
        setText(startingText);

        const newNodes = [];
        const newEdges = [];
        const newAdjList = {};


        const gridSize = Math.ceil(Math.sqrt(numNodes));
        const areaWidth = 500; 
        const areaHeight = 500; 
        const margin = 10;
        const gridSpacingX = (areaWidth - 2 * margin) / gridSize;
        const gridSpacingY = (areaHeight - 2 * margin) / gridSize;

        for (let i = 0; i < numNodes; i++) {
            const row = Math.floor(i / gridSize);
            const col = i % gridSize;
            const x = margin + col * gridSpacingX + Math.random() * gridSpacingX * 0.9;
            const y = margin + row * gridSpacingY + Math.random() * gridSpacingY * 0.9;

            const newNode = {
                id: i,
                x: x,
                y: y,
            };
            newNodes.push(newNode);
            newAdjList[i] = [];
        }

        while (newEdges.length < numEdges) {
            const from = newNodes[Math.floor(Math.random() * numNodes)];
            const to = newNodes[Math.floor(Math.random() * numNodes)];
            if (from.id !== to.id) {
                const edgeExists = newEdges.some(edge => 
                    (edge.from.id === from.id && edge.to.id === to.id) ||
                    (!isDirected && edge.from.id === to.id && edge.to.id === from.id)
                );
                if (!edgeExists) {
                    const newEdge = { from, to, color: defaultEdgeColor };
                    newEdges.push(newEdge);
                    newAdjList[from.id].push(to.id);
                    if(!isDirected){
                        newAdjList[to.id].push(from.id);
                    }
                }
            }
        }

        setNodes(newNodes);
        setEdges(newEdges);
        setAdjList(newAdjList);
        setNodeCount(newNodes.length);
    };

    // Function to reset the graph
    const resetGraph = () => {
        if(algorithmRunning){
            return;
        }
        setNodes([]);
        setEdges([]);
        setClickedMST(false);
        setClickedTraversal(false);
        setIsRemovingEdge(false);
        setText(startingText);
    };

    // Function to remove a selected node form the graph
    const removeNode = () => {
        if (selectedNode == null) return;

        setNodes(nodes.filter(node => node.id !== selectedNode.id));
        setEdges(edges.filter(edge => edge.from.id !== selectedNode.id && edge.to.id !== selectedNode.id));
        setAdjList(prevAdjList => {
            const newAdjList = { ...prevAdjList };
            delete newAdjList[selectedNode.id];
            for (const key in newAdjList) {
                newAdjList[key] = newAdjList[key].filter(id => id !== selectedNode.id);
            }
            return newAdjList;
        });
        setSelectedNode(null);
    };

    // Function to handle clicking on a node
    const handleNodeClick = (node) => {
        if (dragging) {
            return;
        }

        if (isAddingEdge) {
            if (selectedNode && selectedNode.id !== node.id) {
                const edgeExists = edges.some(edge =>
                    (edge.from.id === selectedNode.id && edge.to.id === node.id) ||
                    (!isDirected && edge.from.id === node.id && edge.to.id === selectedNode.id)
                );

                if(!edgeExists){
                    const newEdge = { from: selectedNode, to: node, color: defaultEdgeColor };
                    setEdges(prevEdges => [...prevEdges, newEdge]);

                    setAdjList(prevAdjList => {
                        const newAdjList = { ...prevAdjList };
                        if (!newAdjList[selectedNode.id]) newAdjList[selectedNode.id] = [];
                        if (!newAdjList[node.id]) newAdjList[node.id] = [];
                        newAdjList[selectedNode.id].push(node.id);
                        if (!isDirected) {
                            newAdjList[node.id].push(selectedNode.id);
                        }
                        return newAdjList;
                    })
                }

                setIsAddingEdge(false);
                setSelectedNode(null);
            } else if (selectedNode && selectedNode.id === node.id) {
                setSelectedNode(null);
                setIsAddingEdge(false);
            }
            setText(startingText);
        } else if(isDFS){
            setVisitedNodes([]);
            setVisitedEdges([]);
            dfs(node);
            setIsDFS(false);
        } else if(isBFS){
            setVisitedNodes([]);
            setVisitedEdges([]);
            bfs(node);
            setIsBFS(false);
        }else if(isPrim){
            setIsPrim(false);
            animatePrimsAlgorithm(node);
        }else if(isShortestPath){
            if(!startNode){
                setStartNode(node);
                setText("Select End Node for Shortest Path");
            }else if(!endNode){
                setEndNode(node);
                setText("Finding Shortest Path...");
                findShortestPath(startNode, node);
                setIsShortestPath(false);
            }
        }else if(isTSP){
            setVisitedNodes([]);
            setVisitedEdges([]);
            tsp(node);
            setIsTSP(false);
        }else{
            if (selectedNode && selectedNode.id === node.id) {
                setSelectedNode(null); 
                setText(startingText);
            } else {
                if(!algorithmRunning){
                    setSelectedNode(node);
                }

            }
        }
    }

    // Function to handle mouse down event for dragging
    const handleMouseDown = () => {
        setDragging(false);
    };

    // Function to handle slider change
    const handleSliderChange = (event) => {
        const newValue = event.target.value;
        setSliderValue(newValue);
        sliderValueRef.current = newValue;
    }

    // Function to start removing an edge
    const startRemovingEdge = () => {
        if(algorithmRunning){
            return;
        }

        if(isRemovingEdge){
            setIsRemovingEdge(false);
            setText("");
            return;
        }

        if(edges.length < 1){
            alert("must be at least one edge");
            return;
        }
        setText("Click on an edge to remove it");
        setIsRemovingEdge(true);
    }

    // Function to initiate adding an edge
    const handleAddEdge = () => {
        if (selectedNode === null) return;

        if(!isDirected){
            if(((nodes.length * (nodes.length - 1)) / 2) === edges.length){
                alert("cannot add another edge");
                return;
            }
        }else{
            if(((nodes.length * (nodes.length - 1))) === edges.length){
                alert("cannot add another edge");
                return;
            }
        }


        if (nodes.length < 2) {
            alert("You need at least two nodes to add an edge.");
            return;
        }
        setIsAddingEdge(true);
        setText("Click another node to add edge");
    };

    // Function to handle clicking on an edge
    const handleEdgeClick = (edge) => {
        if (isRemovingEdge) {
            setEdges(edges.filter(e => e !== edge));
            setIsRemovingEdge(false);

            setAdjList(prevAdjList => {
                const newAdjList = { ...prevAdjList };
                newAdjList[edge.from.id] = newAdjList[edge.from.id].filter(id => id !== edge.to.id);
                newAdjList[edge.to.id] = newAdjList[edge.to.id].filter(id => id !== edge.from.id);
                return newAdjList;
            });
            setText(startingText);
        }
    }

    // Function to handle dragging a node
    const handleDrag = (e, data, node) => {
        setDragging(true);
        node.x = data.x;
        node.y = data.y;
        setNodes([...nodes]);
    };

    // Function to handle stopping the drag of a node
    const handleDragStop = () => {
        setTimeout(() => {
            setDragging(false);
        }, 0);
    };

    return {addNode, generateGraph, resetGraph, removeNode, handleNodeClick, handleMouseDown, handleSliderChange, startRemovingEdge, handleAddEdge, handleEdgeClick, handleDrag, handleDragStop};
}
